# -*- coding: utf-8 -*-from bs4 import BeautifulSoupfrom EveryBean import Beanimport reimport jsonbaseUrl='http://itech.ifeng.com/'# 返回DataBeanListdef dealHtml(html):    '''    处理html    :param html:    :return:List<FHBean>    '''    FHList=[]    #获取到的是Str类型    dataList = re.findall(r'getListDatacallback\((.*?)\)', html)[0]    #将Str转换为Json类型    jsonDataList=json.loads(dataList)    #分解输出    for item in jsonDataList:        #获取标题        title =item['title']        print(title)        # 获取内容链接        contentUrl=baseUrl+item['pageUrl']        print(contentUrl)        #获取图片链接        imgUrl='http://'+item['i_thumbnail']        print(imgUrl)        # 生成DataBean并加入到列表中        FHBean=Bean.DataBean(title, contentUrl, imgUrl)        FHList.append(FHBean)        print('==================================================================')    return FHList